const input = $json["body"]["Body"].replace(/\n/g, ' ').toLowerCase();

// Match name, age, location (mandatory) and optionally budget and interest
const match = input.match(
  /name\s*:\s*(\w+)\s*age\s*:\s*(\d+)\s*location\s*:\s*(\w+)(?:\s*budget\s*:\s*(\d+))?(?:\s*interest\s*:\s*([\w\s,]+))?/i
);

if (match) {
  const name = match[1];
  const age = match[2];
  const location = match[3];
  const budget = match[4] || null;
  const interest = match[5] ? match[5].trim() : null;

  return [{
    name,
    age,
    location,
    budget,
    interest,
    from: $json["body"]["From"],
    status: (budget && interest) ? "complete" : "incomplete"
  }];
} else {
  return [{
    error: "‚ùå Invalid format. Please send at least: Name, Age, Location.\n\nExample:\nName: Ravi\nAge: 28\nLocation: Goa\nBudget: 20000\nInterest: Beaches"
  }];
}

